    private void txtbusquedaDeudorKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtbusquedaDeudorKeyTyped
   txtbusquedaDeudor.addKeyListener(new KeyAdapter() {
            public void keyReleased(final KeyEvent e) {
                String cadena = (txtbusquedaDeudor.getText()).toUpperCase();
                txtbusquedaDeudor.setText(cadena);
                repaint();
                filtro();}
        });
        trsfiltro = new TableRowSorter(tbldeudores.getModel());
        tbldeudores.setRowSorter(trsfiltro);          // TODO add your handling code here:
    }//GEN-LAST:event_txtbusquedaDeudorKeyTyped

    private void tbldeudoresMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tbldeudoresMouseClicked
    if(tbldeudores.getValueAt(tbldeudores.getSelectedRow(),tbldeudores.getSelectedColumn())!=null){
    cuentasP.consultarCuotasTotal(tblcuotas,tbldeudores.getValueAt(tbldeudores.getSelectedRow(),0).toString() );
    txtcliente.setText(tbldeudores.getValueAt(tbldeudores.getSelectedRow(),1).toString());
    if (evt.getClickCount() == 2) {
         cedulaClientep=tbldeudores.getValueAt(tbldeudores.getSelectedRow(),0).toString();
        nombreClientep=tbldeudores.getValueAt(tbldeudores.getSelectedRow(),1).toString();
         Pagar_deudor dialog = new Pagar_deudor(new javax.swing.JFrame(), true);
   dialog.setLocationRelativeTo(null);
   dialog.setVisible(true); 
        JOptionPane.showMessageDialog(null, "siii");
        // recargar 
    }}
       // TODO add your handling code here:
    }//GEN-LAST:event_tbldeudoresMouseClicked


