    private void btnseleccionarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnseleccionarActionPerformed
String  nombre;
        int fselec=tblentrenamiento.getSelectedRow();
        if (fselec==-1)JOptionPane.showMessageDialog(null,"seleccione un entrenamiento","Advertencia",JOptionPane.WARNING_MESSAGE);
         else {
            codigo= tblentrenamiento.getValueAt(fselec,0).toString();
            nombre= tblentrenamiento.getValueAt(fselec,1).toString();
            
//if (carEntreno.getselecCargarEn()==0){
           Entrenamiento.txtnombre.setText(nombre);
           Entrenamiento.txtnombre.setEnabled(false);
           obj.consultarplanCargar(codigo, Entrenamiento.tbltabla);
           

//else 
//            Entrenamiento.txtnombre.setText(nombre);
            //Entrenamiento.txtnombreentrenador.setText(nombre);
            
            this.dispose();  }      // TODO add your handling code here:
    }//GEN-LAST:event_btnseleccionarActionPerformed

    private void txtbusquedaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtbusquedaKeyTyped
 txtbusqueda.addKeyListener(new KeyAdapter() {
            public void keyReleased(final KeyEvent e) {
                String cadena = (txtbusqueda.getText()).toUpperCase();
                txtbusqueda.setText(cadena);
                repaint();
                filtro();
            }
        });
        trsfiltro = new TableRowSorter(tblentrenamiento.getModel());
        tblentrenamiento.setRowSorter(trsfiltro);         
    }//GEN-LAST:event_txtbusquedaKeyTyped

