    private void btnselecEntrenadorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnselecEntrenadorActionPerformed
        
        String codigo, des, descripcion, pellido; 
        int fselec=tbltermino.getSelectedRow();
        if (fselec==-1)JOptionPane.showMessageDialog(null,"seleccione un Termino","Advertencia",JOptionPane.WARNING_MESSAGE);
         else {
            codigo= tbltermino.getValueAt(fselec,0).toString();
            descripcion= tbltermino.getValueAt(fselec,1).toString();
            Factura.txttermino.setText(codigo);
           //cargarEntreno obj=new cargarEntreno();
          // obj.hola();
            this.dispose();
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_btnselecEntrenadorActionPerformed

    private void txtbusquedaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtbusquedaKeyTyped
        txtbusqueda.addKeyListener(new KeyAdapter() {
            public void keyReleased(final KeyEvent e) {
                String cadena = (txtbusqueda.getText()).toUpperCase();
                txtbusqueda.setText(cadena);
                repaint();
                filtro();
            }
        });
        trsfiltro = new TableRowSorter(tbltermino.getModel());
        tbltermino.setRowSorter(trsfiltro);        // TODO add your handling code here:
    }//GEN-LAST:event_txtbusquedaKeyTyped

