public void calculakrdex(){
 DefaultTableModel encabezado=(DefaultTableModel) jTable1.getModel();
      //poner la validacion 
       jTable1.setModel(encabezado);
       double cantidad=0,costo=0,total=0;
       double cantidad2=0,costo2=0,total2=0;
       double cantidad3=0,costo3=0,total3=0;
       cantidad=Double.parseDouble(encabezado.getValueAt(0, 4).toString());
       costo=Double.parseDouble(encabezado.getValueAt(0, 5).toString());
       total=Double.parseDouble(encabezado.getValueAt(0, 6).toString());
       encabezado.setValueAt(cantidad, 0, 7);
       encabezado.setValueAt(costo, 0, 8);
       encabezado.setValueAt(total, 0, 9);
       for (int i = 1; i < encabezado.getRowCount(); i++) {
           if (encabezado.getValueAt(i, 2).toString().equals("compra")){
               cantidad2=cantidad+Double.parseDouble(encabezado.getValueAt(i, 4).toString());
               total2=total+Double.parseDouble(encabezado.getValueAt(i, 6).toString());
               costo2=total2/cantidad2;
               encabezado.setValueAt(cantidad2, i, 7);
               encabezado.setValueAt(costo2, i, 8);
               encabezado.setValueAt(total2, i, 9);
           }
           else {
               encabezado.setValueAt(encabezado.getValueAt(i-1, 8).toString(), i, 5);
               total3=Double.parseDouble(encabezado.getValueAt(i-1, 8).toString())*Double.parseDouble(encabezado.getValueAt(i, 4).toString());
               encabezado.setValueAt(total3, i, 6);
               cantidad2=cantidad-Double.parseDouble(encabezado.getValueAt(i, 4).toString());
               total2=total-Double.parseDouble(encabezado.getValueAt(i, 6).toString());
               costo2=total2/cantidad2;
               encabezado.setValueAt(cantidad2, i, 7);
               encabezado.setValueAt(costo2, i, 8);
               encabezado.setValueAt(total2, i, 9);
           }
           cantidad=cantidad2;
           total=total2;
           
        
    }
   
}

