    private void foto_labelMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_foto_labelMouseClicked
 if (TXTnombre.isEnabled()){
        txt_direccion=null;
        File archivo;
        JFileChooser flcAbrirArchivo = new JFileChooser();
        flcAbrirArchivo.setFileFilter(new FileNameExtensionFilter("Archivo de Imagen", "jpg", "jpeg", "png"));
        int respuesta = flcAbrirArchivo.showOpenDialog(this);

        if (respuesta == JFileChooser.APPROVE_OPTION) {
            archivo = flcAbrirArchivo.getSelectedFile();
            txt_direccion=archivo.getAbsolutePath();
            Image foto = getToolkit().getImage(txt_direccion);
            foto = foto.getScaledInstance(160, 165, 1);
            foto_label2.setIcon(new ImageIcon(foto));
        }
universales();}
    }//GEN-LAST:event_foto_labelMouseClicked

