    private void TXTcedulaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_TXTcedulaKeyTyped
       
          try {
            Double.parseDouble(TXTcedula.getText()+evt.getKeyChar());
            if (validar.VerificaCedula(TXTcedula.getText()+evt.getKeyChar())) {
                    TXTnombre.setEnabled(true);
                    TXTapellido.setEnabled(true);
                    TXTtelefono.setEnabled(true);
                    TXTdireccion.setEnabled(true);
                    TXTemail.setEnabled(true);
                    fechaInic.setEnabled(true);
                    fecnacimiento.setEnabled(true);
                    comboProvincia.setEnabled(true);
                    foto_label2.setEnabled(true);
                    LSTsexo.setEnabled(true);
            } 
        } catch (Exception e) {
            evt.consume();
        }

universales();
    }//GEN-LAST:event_TXTcedulaKeyTyped

