public static boolean parametroBasio(){
    if (TXTnombre.getText().equals("")||TXTapellido.getText().equals("")||TXTcedula.getText().equals("")||TXTdireccion.getText().equals("")||TXTemail.getText().equals("")||LSTsexo.getSelectedIndex()==0||fechaInic.getDate()==null||fecnacimiento.getDate()==null||comboProvincia.getSelectedIndex()<1)
        return true; 
    else 
        return false ;}

public static void getDatosIngresados(){
    cedula= Integer.parseInt(TXTcedula.getText());
    nombre=TXTnombre.getText();
    apellido=TXTapellido.getText();
    direccion=TXTdireccion.getText();
    telefono=TXTtelefono.getText();
    email=TXTemail.getText();
  //  convertido = fecha.format(fechaInic.getCalendar().getTime());
     //Formato
    fecha1 = fechaInic.getDate();
    fecha2 = fecnacimiento.getDate();
    fecInicio=fecha.format(fecha1);
    fecNacimiento=fecha.format(fecha2);
    sexo=(String) LSTsexo.getSelectedItem();
    
   
}
public static boolean checkEmail (String email) {

    // Establecer el patron
    Pattern p = Pattern.compile("[-\\w\\.]+@[\\.\\w]+\\.\\w+");

    // Asociar el string al patron
    Matcher m = p.matcher(email);

   // Comprobar si encaja
   return m.matches();

}
