public static void guardarFederados (){
  
        if(parametroBasio()){
            JOptionPane.showMessageDialog(null,"Faltan Parametros a ingresar","Error",JOptionPane.ERROR_MESSAGE);}
        else {
          if (!validar.VerificaCedula(TXTcedula.getText())) {
             JOptionPane.showMessageDialog(null, "Error al digitar Cedula","Error",JOptionPane.ERROR_MESSAGE); 
             TXTcedula.requestFocus();} 
          else {getDatosIngresados();
            try {
                if(TXTcedula.isEnabled()==false){
                    if(txt_direccion==null)obj.modificarFederadosSinfoto(String.valueOf(cedula), nombre, apellido, telefono, direccion, email, sexo, fecInicio, fecNacimiento,comboProvincia.getSelectedItem().toString());
                    else obj.modificarFederados(String.valueOf(cedula), nombre, apellido, telefono, direccion, email, sexo, fecInicio, fecNacimiento,txt_direccion,comboProvincia.getSelectedItem().toString());
                           
                }
                else
                obj.ingresarFederado(cedula, nombre, apellido, telefono, direccion, email, sexo, fecInicio, fecNacimiento,txt_direccion,comboProvincia.getSelectedItem().toString());
                nuevo();
            } catch (IOException ex) {
                Logger.getLogger(Entrenador.class.getName()).log(Level.SEVERE, null, ex);
            }}

        }
}
